/* eslint-disable no-undef */
import React from 'react';
import { View, Text, Image, StyleSheet, Button } from 'react-native';

const styles = StyleSheet.create({
  item: {
    display: 'flex',
    flexGrow: 1,
    flexShrink: 1,
    flexDirection: 'row',
    alignContent: 'stretch',
    alignItems: 'flex-start',
    flexWrap: 'wrap',
    height: 150,
    marginTop: 0,
    backgroundColor: '#ffffff'
  },
  tinyLogo: {
    width: 50,
    height: 50,
    marginTop: 10,
    marginLeft: 10,
    borderRadius: 5,
  },
  header: {
    width: '80%',
    height: '60%',
    alignItems: 'flex-start',
    backgroundColor: "#ffeedd"
  },
  imageContainer: {
    width: '20%',
    height: '60%',
    backgroundColor: "#ff34dd"
  },
  footer: {
    width: '100%',
    height: '21%',
    paddingLeft:'7%',
    display: 'flex',
    flexDirection: 'row',
    alignContent:'space-between',
    backgroundColor: "#ffee00"
  },
  footerTitles: {
    width: '100%',
    paddingLeft:'7%',
    display: 'flex',
    flexDirection: 'row',
    backgroundColor: "#ff6700"
  },
  fullname: {
    width: '100%',
    marginTop: 10,
    marginLeft: 20,
    fontSize: 16,
    fontWeight: '600',
  },
  description: {
    marginTop: 7,
    marginLeft: 20,
    color: 'silver',
    fontSize: 14,
    fontWeight: '300',
  },
  language: {
    marginTop: 7,
    marginLeft: 20,
    height: 10,
  },

});

const convertNumber = (data) => {
  let result = '';

  if (data >= 1000) {
    data /= 1000;
    data = Math.round(data * 10) / 10;
    result = `${data}k`;
  } else {
    result = data;
  }


  return result;
};

const RepositoryItem = ({ item }) => {

  return (
    <View style={styles.item}>
      <View style={styles.imageContainer}>
        <Image style={styles.tinyLogo} source={{ uri: item.ownerAvatarUrl }} />
      </View>

      <View style={styles.header}>
        <Text style={styles.fullname} >{item.fullName}</Text>
        <Text style={styles.description} >{item.description}</Text>
        <View style={styles.language}>
          <Button
            title={item.language}
            color="darkblue"
          />
        </View>
      </View>

      <View style={styles.footer}>
        <Text style={styles.fullname} >{convertNumber(item.stargazersCount)}</Text>
        <Text style={styles.fullname} >{convertNumber(item.forksCount)}</Text>
        <Text style={styles.fullname} >{convertNumber(item.reviewCount)}</Text>
        <Text style={styles.fullname} >{convertNumber(item.ratingAverage)}</Text>
      </View>

      <View style={styles.footerTitles}>
        <Text style={styles.fullname} >Starts</Text>
        <Text style={styles.fullname} >Forks</Text>
        <Text style={styles.fullname} >Reviews</Text>
        <Text style={styles.fullname} >{convertNumber(item.ratingAverage)}</Text>
      </View>
    </View>
  );
};


