/* eslint-disable no-undef */
import React from 'react';
import { View, Text, Image, StyleSheet, Button } from 'react-native';

const styles = StyleSheet.create({
  item: {
    display: 'flex',
    flexGrow: 1,
    flexShrink: 1,
    flexDirection: 'row',
    alignContent: 'stretch',
    alignItems: 'flex-start',
    flexWrap: 'wrap',
    height: 160,
    marginTop: 0,
    backgroundColor: '#ffffff'
  },
  tinyLogo: {
    width: 50,
    height: 50,
    marginTop: 10,
    marginLeft: 12,
    borderRadius: 5,
  },
  header: {
    width: '80%',
    height: '57%',
    alignItems: 'flex-start',
    // backgroundColor: "#ffeedd"
  },
  imageContainer: {
    width: '15%',
    height: '57%',
  //  backgroundColor: "#ff34dd"
  },
  footer: {
    marginTop:20,
    width: '100%',
    height: '40%',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'stretch',
    alignContent: 'center',
    // backgroundColor: "#ffee00"
  },
  footerNumbersContainer: {
    width: '100%',
    marginRight:80,
    paddingLeft: '4%',
    paddingRight: '4%',
    justifyContent: 'space-between',
    flexDirection: 'row',
    // backgroundColor: "#ff6798"
  },
  footerNumbersText: {
    width:100,
    textAlign:'center',
    fontSize: 16,
    fontWeight: '600',
  },
  footerTitleContainer: {
    width: '100%',
    marginTop:5,
    paddingLeft: '4%',
    paddingRight: '4%',
    justifyContent: 'space-between',
    flexDirection: 'row',
    // backgroundColor: "#ff6700",
  },
  footerTitlesText: {
    width:100,
    textAlign:'center',
    fontSize: 13,
    fontWeight: '400',
    color:'gray'
  },
  fullname: {
    width: '100%',
    marginTop: 10,
    fontSize: 20,
    fontWeight: '600',
  },
  description: {
    marginTop: 7,
    color: 'silver',
    fontSize: 14,
    fontWeight: '300',
  },
  language: {
    marginTop: 7,
    height: 10,
  },

});

const convertNumber = (data) => {
  let result = '';

  if (data >= 1000) {
    data /= 1000;
    data = Math.round(data * 10) / 10;
    result = `${data}k`;
  } else {
    result = data;
  }


  return result;
};

const RepositoryItem = ({ item }) => {

  return (
    <View style={styles.item}>
      <View style={styles.imageContainer}>
        <Image style={styles.tinyLogo} source={{ uri: item.ownerAvatarUrl }} />
      </View>

      <View style={styles.header}>
        <Text style={styles.fullname} >{item.fullName}</Text>
        <Text style={styles.description} >{item.description}</Text>
        <View style={styles.language}>
          <Button
            title={item.language}
            color="darkblue"
          />
        </View>
      </View>

      <View style={styles.footer}>
        <View style={styles.footerNumbersContainer}>
          <Text style={styles.footerNumbersText} >{convertNumber(item.stargazersCount)}</Text>
          <Text style={styles.footerNumbersText} >{convertNumber(item.forksCount)}</Text>
          <Text style={styles.footerNumbersText} >{convertNumber(item.reviewCount)}</Text>
          <Text style={styles.footerNumbersText} >{convertNumber(item.ratingAverage)}</Text>
        </View>
        <View style={styles.footerTitleContainer}>
          <Text style={styles.footerTitlesText} >Starts</Text>
          <Text style={styles.footerTitlesText} >Forks</Text>
          <Text style={styles.footerTitlesText} >Reviews</Text>
          <Text style={styles.footerTitlesText} >Ratings</Text>
        </View>
      </View>
    </View>
  );
};


